import React from 'react';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import { Card } from 'react-bootstrap';

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
);

// eslint-disable-next-line import/no-anonymous-default-export
export default function (props) {
    const options = {
        responsive: true,
        maintainAspectRatio: false,
        indexAxis: 'x',
        elements: {
            bar: {
                borderWidth: 2,
            },
        },
        plugins: {
            legend: {
                position: 'bottom',
            },
            title: {
                display: true,
                text: 'Ordens de coleta por mês',
            },
        },
    };
    const labels = []

    if (props.data) {
        if (props.data.length > 0) {
            props.data.map((row, i) => {
                if (i === 0) {
                    Object.keys(row.data).map((r) => {
                        labels.push(r)
                    })
                }
            })
        }
    }

    const sets = []

    if (props.data) {
        if (props.data.length > 0) {
            props.data.map((row, i) => {
                let data = []

                // row.data.map((r) => {
                //     data.push(r)
                // })
                console.log(row.data)

                // sets.push({
                //     label: row.name,
                //     data: data,
                //     borderColor: 'rgb(0, 255, 212)',
                //     backgroundColor: 'rgba(0, 255, 212, 0.5)',
                // })
            })
        }
        console.log(sets)
    }

    const data = {
        labels,
        datasets: [
            {
                label: 'Ordens',
                data: props.data.values,
                borderColor: 'rgb(0, 255, 212)',
                backgroundColor: 'rgba(0, 255, 212, 0.5)',
            },
        ],
    };

    return <Card>
        <Card.Body>
            <Bar height={300} options={options} data={data} />
        </Card.Body>
    </Card>
}
